styles.css.content:
/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Poppins', sans-serif;
    line-height: 1.6;
    color: #333;
    background-color: #fefefe;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

/* Header Styles */
.header {
    background: linear-gradient(135deg, #FFB6C1 0%, #FFC0CB 100%);
    padding: 1rem 0;
    box-shadow: 0 2px 20px rgba(255, 182, 193, 0.3);
    position: sticky;
    top: 0;
    z-index: 1000;
}

.nav-wrapper {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo h1 {
    font-family: 'Playfair Display', serif;
    font-size: 2rem;
    font-weight: 700;
    color: #FFD1DC;
    margin-bottom: 0.2rem;
}

.logo .tagline {
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.9);
    font-weight: 300;
}

.nav-menu {
    display: flex;
    align-items: center;
    gap: 2rem;
}

.nav-link {
    color: #fff;
    text-decoration: none;
    font-weight: 500;
    transition: color 0.3s ease;
}

.nav-link:hover {
    color: rgba(255, 255, 255, 0.8);
}

.cart-icon {
    position: relative;
    color: #fff;
    font-size: 1.2rem;
    cursor: pointer;
}

.cart-count {
    position: absolute;
    top: -8px;
    right: -8px;
    background: #e91e63;
    color: white;
    border-radius: 50%;
    width: 20px;
    height: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.8rem;
    font-weight: 600;
}

/* Hero Section */
.hero {
    background: linear-gradient(135deg, #FFE4E6 0%, #FFF0F1 100%);
    padding: 4rem 0;
    min-height: 70vh;
    display: flex;
    align-items: center;
}

.hero-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 3rem;
    align-items: center;
}

.hero-text h2 {
    font-family: 'Playfair Display', serif;
    font-size: 3rem;
    font-weight: 700;
    color: #2c3e50;
    margin-bottom: 1.5rem;
    line-height: 1.2;
}

.hero-text p {
    font-size: 1.1rem;
    color: #666;
    margin-bottom: 2rem;
    line-height: 1.7;
}

.discount-banner {
    background: linear-gradient(135deg, #e91e63, #ff4081);
    color: white;
    padding: 1rem 2rem;
    border-radius: 50px;
    display: inline-block;
    margin-bottom: 2rem;
    box-shadow: 0 4px 20px rgba(233, 30, 99, 0.3);
}

.discount-text {
    font-size: 1.2rem;
    font-weight: 700;
    display: block;
}

.discount-subtitle {
    font-size: 0.9rem;
    opacity: 0.9;
}

.cta-button {
    background: linear-gradient(135deg, #FFB6C1, #FFC0CB);
    color: white;
    padding: 1rem 2.5rem;
    border: none;
    border-radius: 50px;
    font-size: 1.1rem;
    font-weight: 600;
    text-decoration: none;
    display: inline-block;
    transition: all 0.3s ease;
    box-shadow: 0 4px 20px rgba(255, 182, 193, 0.4);
}

.cta-button:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 25px rgba(255, 182, 193, 0.6);
}

.hero-product-showcase {
    background: linear-gradient(135deg, #FFB6C1, #FFC0CB);
    height: 300px;
    border-radius: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    color: white;
    box-shadow: 0 10px 40px rgba(255, 182, 193, 0.3);
}

.hero-icon {
    font-size: 4rem;
    margin-bottom: 1rem;
}

/* Products Section */
.products-section {
    padding: 5rem 0;
    background: #fafafa;
}

.section-title {
    font-family: 'Playfair Display', serif;
    font-size: 2.5rem;
    font-weight: 700;
    text-align: center;
    color: #2c3e50;
    margin-bottom: 1rem;
}

.section-subtitle {
    text-align: center;
    color: #666;
    font-size: 1.1rem;
    margin-bottom: 3rem;
}

.products-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 2rem;
    margin-top: 3rem;
}

.product-card {
    background: white;
    border-radius: 20px;
    overflow: hidden;
    box-shadow: 0 5px 30px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
}

.product-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.15);
}

.product-image {
    position: relative;
    height: 250px;
    background: linear-gradient(135deg, #FFE4E6, #FFF0F1);
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
}

.product-img {
    width: 100%;
    height: 100%;
    object-fit: contain;
    border-radius: 0;
}

.discount-badge {
    position: absolute;
    top: 15px;
    right: 15px;
    background: #e91e63;
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-weight: 600;
    font-size: 0.9rem;
}

.product-placeholder {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    color: #FFB6C1;
    text-align: center;
}

.product-placeholder i {
    font-size: 3rem;
    margin-bottom: 0.5rem;
}

.product-placeholder span {
    font-weight: 600;
    font-size: 1.1rem;
}

/* Product-specific colors */
.thermal-brush {
    color: #ff6b35;
}

.blow-dry-black {
    color: #2c3e50;
}

.thermal-pink {
    color: #e91e63;
}

.mini-pro {
    color: #9c27b0;
}

.styling-brush {
    color: #795548;
}

.quick-dry {
    color: #00bcd4;
}

.product-info {
    padding: 2rem;
}

.product-title {
    font-family: 'Playfair Display', serif;
    font-size: 1.4rem;
    font-weight: 600;
    color: #2c3e50;
    margin-bottom: 1rem;
}

.product-description p {
    color: #666;
    line-height: 1.6;
    margin-bottom: 1.5rem;
}

.product-features {
    margin-bottom: 1.5rem;
}

.feature {
    display: flex;
    align-items: center;
    margin-bottom: 0.5rem;
    color: #555;
}

.feature i {
    color: #FFB6C1;
    margin-right: 0.8rem;
    font-size: 0.9rem;
    width: 16px;
}

.feature span {
    font-size: 0.9rem;
}

.product-pricing {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin-bottom: 1.5rem;
    flex-wrap: wrap;
}

.original-price {
    color: #999;
    text-decoration: line-through;
    font-size: 1rem;
}

.sale-price {
    color: #e91e63;
    font-size: 1.5rem;
    font-weight: 700;
}

.savings {
    background: #4caf50;
    color: white;
    padding: 0.3rem 0.8rem;
    border-radius: 15px;
    font-size: 0.8rem;
    font-weight: 600;
}

.add-to-cart-btn {
    width: 100%;
    background: linear-gradient(135deg, #FFB6C1, #FFC0CB);
    color: white;
    border: none;
    padding: 1rem 2rem;
    border-radius: 50px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
}

.add-to-cart-btn:hover {
    background: linear-gradient(135deg, #ff9bb3, #ffadc1);
    transform: translateY(-2px);
}

/* About Section */
.about-section {
    padding: 5rem 0;
    background: white;
}

.about-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 3rem;
    align-items: center;
}

.about-text h2 {
    font-family: 'Playfair Display', serif;
    font-size: 2.5rem;
    font-weight: 700;
    color: #2c3e50;
    margin-bottom: 1.5rem;
}

.about-text p {
    color: #666;
    line-height: 1.7;
    margin-bottom: 2rem;
    font-size: 1.1rem;
}

.about-features {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.about-feature {
    display: flex;
    align-items: flex-start;
    gap: 1rem;
}

.about-feature i {
    color: #FFB6C1;
    font-size: 1.5rem;
    margin-top: 0.3rem;
}

.about-feature h4 {
    color: #2c3e50;
    margin-bottom: 0.5rem;
    font-weight: 600;
}

.about-feature p {
    color: #666;
    font-size: 0.95rem;
    margin: 0;
}

.about-showcase {
    background: linear-gradient(135deg, #FFE4E6, #FFF0F1);
    height: 300px;
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #FFB6C1;
    font-size: 4rem;
}

/* Contact Section */
.contact-section {
    padding: 5rem 0;
    background: #fafafa;
}

.contact-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 3rem;
    margin-top: 3rem;
}

.contact-info {
    display: flex;
    flex-direction: column;
    gap: 2rem;
}

.contact-item {
    display: flex;
    align-items: flex-start;
    gap: 1rem;
}

.contact-item i {
    color: #FFB6C1;
    font-size: 1.5rem;
    margin-top: 0.3rem;
}

.contact-item h4 {
    color: #2c3e50;
    margin-bottom: 0.5rem;
    font-weight: 600;
}

.contact-item p {
    color: #666;
    margin: 0;
}

.contact-form {
    background: white;
    padding: 2rem;
    border-radius: 20px;
    box-shadow: 0 5px 30px rgba(0, 0, 0, 0.1);
}

.form-group {
    margin-bottom: 1.5rem;
}

.form-group input,
.form-group textarea {
    width: 100%;
    padding: 1rem;
    border: 2px solid #f0f0f0;
    border-radius: 10px;
    font-size: 1rem;
    transition: border-color 0.3s ease;
}

.form-group input:focus,
.form-group textarea:focus {
    outline: none;
    border-color: #FFB6C1;
}

.submit-btn {
    background: linear-gradient(135deg, #FFB6C1, #FFC0CB);
    color: white;
    border: none;
    padding: 1rem 2rem;
    border-radius: 50px;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    width: 100%;
}

.submit-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 20px rgba(255, 182, 193, 0.4);
}

/* Footer */
.footer {
    background: #2c3e50;
    color: white;
    padding: 3rem 0 1rem;
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-bottom: 2rem;
}

.footer-section h3,
.footer-section h4 {
    margin-bottom: 1rem;
    color: #FFB6C1;
}

.footer-section p {
    color: #bbb;
    line-height: 1.6;
    margin-bottom: 1rem;
}

.footer-section ul {
    list-style: none;
}

.footer-section ul li {
    margin-bottom: 0.5rem;
}

.footer-section ul li a {
    color: #bbb;
    text-decoration: none;
    transition: color 0.3s ease;
}

.footer-section ul li a:hover {
    color: #FFB6C1;
}

.social-links {
    display: flex;
    gap: 1rem;
    margin-top: 1rem;
}

.social-links a {
    color: #bbb;
    font-size: 1.2rem;
    transition: color 0.3s ease;
}

.social-links a:hover {
    color: #FFB6C1;
}

.newsletter-form {
    display: flex;
    gap: 0.5rem;
    margin-top: 1rem;
}

.newsletter-form input {
    flex: 1;
    padding: 0.8rem;
    border: none;
    border-radius: 25px;
    font-size: 0.9rem;
}

.newsletter-form button {
    background: #FFB6C1;
    color: white;
    border: none;
    padding: 0.8rem 1.5rem;
    border-radius: 25px;
    cursor: pointer;
    font-weight: 600;
}

.footer-bottom {
    border-top: 1px solid #444;
    padding-top: 1rem;
    text-align: center;
    color: #bbb;
}

.footer-bottom a {
    color: #FFB6C1;
    text-decoration: none;
}

/* Responsive Design */
@media (max-width: 768px) {
    .nav-wrapper {
        flex-direction: column;
        gap: 1rem;
    }

    .hero-content {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .hero-text h2 {
        font-size: 2.2rem;
    }

    .about-content {
        grid-template-columns: 1fr;
    }

    .contact-content {
        grid-template-columns: 1fr;
    }

    .products-grid {
        grid-template-columns: 1fr;
    }

    .newsletter-form {
        flex-direction: column;
    }

    .product-pricing {
        flex-direction: column;
        align-items: flex-start;
        gap: 0.5rem;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 0 15px;
    }

    .hero {
        padding: 2rem 0;
    }

    .hero-text h2 {
        font-size: 1.8rem;
    }

    .section-title {
        font-size: 2rem;
    }

    .products-grid {
        grid-template-columns: 1fr;
        gap: 1.5rem;
    }

    .product-card {
        margin: 0 10px;
    }
}

/* Animation Effects */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.product-card {
    animation: fadeInUp 0.6s ease-out;
}

.product-card:nth-child(even) {
    animation-delay: 0.1s;
}

.product-card:nth-child(odd) {
    animation-delay: 0.2s;
}

/* Scroll Effects */
@media (prefers-reduced-motion: no-preference) {
    html {
        scroll-behavior: smooth;
    }
}

/* Additional Hover Effects */
.product-image:hover .product-placeholder {
    transform: scale(1.05);
    transition: transform 0.3s ease;
}

.feature:hover i {
    color: #e91e63;
    transition: color 0.3s ease;
}

/* Loading States */
.add-to-cart-btn:active {
    transform: scale(0.98);
}

/* Focus States for Accessibility */
.nav-link:focus,
.cta-button:focus,
.add-to-cart-btn:focus,
.submit-btn:focus {
    outline: 2px solid #FFB6C1;
    outline-offset: 2px;
}

/* Print Styles */
@media print {
    .header,
    .footer,
    .contact-section {
        display: none;
    }

    .product-card {
        break-inside: avoid;
        box-shadow: none;
        border: 1px solid #ddd;
    }
}
